INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1527, 214, 19120305, 'Die erste Gründung der Schachgruppe erfolgte am 5.
März 1912 in Berlin. Zunächst wurde in einem Lokal an der Cöpenicker Straße gespielt. Durch den ersten Weltkrieg wurde der Spielbetrieb unterbrochen.', 'Festschrift 85 Jahre OSRAM-Schach', '/archiv/chronik/vereine/dok/svosram/festschrift.pdf', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1528, 214, 1918, 'In den Jahren nach 1918 wurde der Spielbetrieb im Café "Astoria" am Potsdamer Platz wieder aufgenommen, allerdings führte die Inflationszeit der Jahre 1922 / 1923 zu einem erneuten Erliegen aller
Aktivitäten.', 'Festschrift 85 Jahre OSRAM-Schach', '/archiv/chronik/vereine/dok/svosram/festschrift.pdf', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1529, 214, 19251002, 'Am 2. Oktober 1925 erfolgte die erneute Wiedergründung der Schachgruppe mit 15 Mitgliedern. Im Laufe der folgenden Jahre stieg die Mitgliederzahl bis auf ca. 60 an und Anfang der Dreißiger Jahre waren 43 Schachfreunde aktiv. Das erste Spiellokal in dieser Phase der Vereinsgeschichte war das "Löwenbräu" am Gendarmenmarkt.', 'Festschrift 85 Jahre OSRAM-Schach', '/archiv/chronik/vereine/dok/svosram/festschrift.pdf', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1530, 214, 192512, 'Im Dezember 1925 zog die SVO-Schachgruppe in das legendäre Café "König" um, welches Unter den Linden Ecke Friedrichstraße beheimatet war. Das Café "König" war zu jener Zeit das Verbandscafé des Berliner Schachverbandes, in dem auch zahlreiche andere Berliner Vereine spielten.', 'Festschrift 85 Jahre OSRAM-Schach', '/archiv/chronik/vereine/dok/svosram/festschrift.pdf', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1531, 214, 1929, 'Im Jahre 1929 erfolgte der Beitritt der SVO-Schachabteilung zum Berliner und Brandenburgischen Schachverband.', 'Festschrift 85 Jahre OSRAM-Schach', '/archiv/chronik/vereine/dok/svosram/festschrift.pdf', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1532, 214, 1932, 'Seit dem Jahre 1932 beteiligte sich die SVO an den Veranstaltungen des Verbandes und hatte dabei auch verhältnismäßig guten Erfolg.', 'Festschrift 85 Jahre OSRAM-Schach', '/archiv/chronik/vereine/dok/svosram/festschrift.pdf', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1533, 214, 1933, 'Sportverein <b>Osram</b> Restaurant zur Sonnenuhr, an der Oberbaumbrücke', 'Ranneforths Schachkalender 1934, S.55-57', '/archiv/chronik/1933/adressen/vereine.html', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1534, 214, 193309, 'Im September 1933 erfolgte dann ein weiterer Wechsel der Spielstätte. Man zog in das Restaurant "Zur Sonnenuhr" nahe der Oberbaumbrücke im Bezirk Friedrichshain um. Unterlagen über die Nutzung des SVO-Sportgeländes an der Spree durch die SVO-Schachabteilung liegen nicht vor.', 'Festschrift 85 Jahre OSRAM-Schach', '/archiv/chronik/vereine/dok/svosram/festschrift.pdf', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1535, 214, 1934, '<b>Osram</b> Sportverein, Klubheim des SV Osram, Berlin-Niederschöneweide, Sedanstr.52 (S-Bhf.Oberspree)', 'Ranneforths Schachkalender 1935, S.60/61', '/archiv/chronik/1934/adressen/vereine.html', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1536, 214, 1934, 'Zum 25jährigen Jubiläum der SV Osram (1909 gegründet) gelobte die Schachgruppe in der damaligen Festschrift, sich die "Pflege und Förderung des Schachspiels und der Kameradschaft zur Aufgabe" gestellt zu haben.', 'Festschrift 85 Jahre OSRAM-Schach', '/archiv/chronik/vereine/dok/svosram/festschrift.pdf', 1);
