INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1616, 237, 1947, 'Im Winterturnier der Schachgruppe Tiergarten siegte H.Kurz mit 11 Punkten vor Klaus Kluge und B.Redlich. In der B-Gruppe sicherten sich die beiden Nachwuchsspieler G.Karan (17 Punkte) und G.Hardtke (16½ Punkte) den Aufstieg zur A-Gruppe. Von den 16 Teilnehmern der C-Gruppe wurde H.Witte mit 14½ Punkten Erster vor K.Tetzner und H.Materne.', 'Schach-Express 4/1947, S.62', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1617, 237, 1947, 'Ein Wettkampf gegen die Schachgruppe Mitte wurde von Tiergarten mit 14:6 Punkten gewonnen.', 'Schach-Express 4/1947, S.62', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1618, 237, 1947, 'Die im Februar 1947 neugegründete Schachgruppe Lichtenberg betätigt sich eifrig in Freundschaftsspielen. Zwar gingen bisher alle Kämpfe verloren (gegen Friedrichshain 4:6, Tiergarten 2½:7½, Tiergarten-Süd 4:5, 1 H.) aber so jung, wie die Mannschaft ist, so eifrig ist sie auch, um zur neuen Runde gewappnet zu sein.<br>Das Gründungsturnier gewann Gröninck mit 9½ Punkten.<br>Für ihre Jugendlichen sucht die Schachgruppe Spielabschlüsse und bittet um Zuschriften.<br>Spiellokal: Rathaus Lichtenberg, Möllendorffstr.<br>Für Männer: Freitags ab 19 Uhr, für Jugendliche: Freitags ab 17 Uhr.', 'Schach-Express 8/1947, S.126', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1619, 237, 1948, 'Die Gruppen Tiergarten und Neukölln nahmen eine alte schöne Sitte wieder auf und veranstalteten einen Sommerausflug zum Prinzengarten, Müggelsee. Im Blitzturnier siegte dabei Kinzel (N.) vor Kluge (T.); den Mannschaftsblitzkampf gewann T. mit 23:21.', 'Deutsche Schachblätter Nr.5 August 1948, S.77', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1620, 237, 194902, 'Schlußstand der Mannschaftsmeisterschaft: Wedding 68½, Prenzlauer Berg 67½, Pankow 61, Tiergarten 59½, Spandau 57, Charlottenburg 54½, Wilmersdorf 53½, Steglitz, Friedrichshain 52½, Friedenau 50½, Neukölln 49, Prenzlauer Berg II 34 (mit Meister B.Koch an der Spitze) verlor also den Titel denkbar knapp an Wedding (mit Gumprich). Eigentlich entschied die zweite Mannschaft von Prenzlauer Berg den Wettkampf, indem sie gegen Wedding mit 0:10 verlor, gegen ihre "Brudermannschaft" aber hartnäckigen Widerstand leistete und nur  3½:6½ im Nachteil blieb.', 'Deutsche Schachblätter Nr.3 März 1949, S.47', '', 1);
