INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1626, 239, 19480815, 'Heute fand die Siegerehrung der Berliner Schachsparte im Heide-Casino Oberschöneweide, für ihre diesjährigen Meister (Frauen: Redlich, Männer: K.Richter, Jugendliche: P.Bares) statt. Nach einer Ansprache des Hauptspartenleiters Hänicke überreichte Hauptspielleiter Schwenke die Siegerurkunden. In einem anschließenden Mannschaftsblitzturnier siegte Friedrichshain knapp vor Turm. Ein Tanz im Freien beendete die wohlgelungene Veranstaltung.', 'Deutsche Schachblätter Nr.7 Oktober 1948, S.110', '', 1);
