INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1652, 252, 195101, 'Seit Januar läuft ein Ermittlungs-Turnier, um eine Übersicht über die Spielstärke des jungen Vereins zu gewinnen. Spielabend: Montag 19.30 Uhr im Neuen Lokal Putbuser Straße 24 (Gesundbrunnen). Erbeten werden Angebote von C-Klassen-Mannschaften für Freundschaftskämpfe an N 31, Ackerstr. 139.', 'BSV-Mitteilungsblatt Nr.2/1951 Mai', '', 1);
