INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1679, 257, 191906, 'In der Freien Vereinigung der Groß-Berliner Schachvereine sind die Mannschaftskämpfe um den von der Berliner Schachgesellschaft gestifteten Wanderpreis in vollem Gange. Es beteiligen sich 14 Vereine mit 27 Mannschaften. Die Ergebnisse der 3.Klasse waren bisher: Doppelbauer 8 Punkte gegen Tegel 2 und A.E.G. 2, Sportklub Charlottenburg 8 gegen Schiller-Realgymnasium 2, Oberspree 4 und Karlshorst 3½ gegen Süd-West 2½ und Neukölln 2, Doppelbauer 9 gegen Sportklub Charlottenburg 3, Springer 5 gegen Neukölln 3 und A.E.G. 1, Schachgesellschaft 6½ gegen Oberspree 4½ und Karlshorst 1. In der 2.Klasse erzielten: Schachverein 6 gegen Springer 3 und Bauer 3, Schachverein 4½ gegen Neukölln 4½ und A.E.G. 0, Schachgesellschaft 7 gegen Anderssen 3 und Tegel 1. Die nächsten Wettspiele fanden bzw. finden statt: 1.Klasse am 13.Juni in der Schachgesellschaft; 2.Klasse am 18.Juni, 24.Juni und 1.Juli in der Schachgesellschaft, 16.Juni im Schachverein, 20.Juni in Süd-West und 2.Juli im Bauer; 3.Klasse 17.Juni im Doppelbauer, 18.Juni in A.E.G., 19.Juni und 30.Juni im Schachverein, 20.Juni, 25.Juni und 3.Juli in der Schachgesellschaft, 3.Juli in Neukölln und 4.Juli im Springer.', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.24/26 vom 22.Juni 1919, S.163/164', '/archiv/bsz/0081-0090/index.html#bsz0088', 1);
