INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1711, 259, 1951, 'Schachklub "Humboldt" teilt Lokalwechsel mit. Es wird jeden Montag gespielt in "Der Insel", Berlin N 31, Voltastraße 19. Sonntags freier Schachverkehr. Ein Freundschaftswettkampf gegen "König Tegel" an 20 Brettern endete 16:4 für "Humboldt".', 'BSV-Mitteilungsblatt Nr.3/1951 Juni', '', 1);
