INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1713, 259, 1951, '<b>Läufer Reinickendorf:</b> Klubmeister 1951 Horst Michel, Blitzmeister 1951 Horst Michel, Jugendmeister 1951 Unverzagt, K.O. Sieger 1951 Dally. Vergleichskampf gegen Reinickendorf West hin 11½ - 5½ für Läufer Reinickendorf, zurück 10½ - 3½ für Läufer Reinickendorf. Vergleichskampf gegen Humboldt hin 13½ - 11½ für Läufer Reinickendorf, zurück 7½ - 9½ für Humboldt.', 'BSV-Mitteilungsblatt Nr.8 November 1951', '', 1);
