INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1722, 262, 1951, 'Durch Siege in Vergleichstreffen gegen Treptow und Springer-Tegel wurden die Neuköllner Schachfreunde inoffizieller Meister der C-Klasse der Mannschaftskämpfe.', 'BSV-Mitteilungsblatt Nr.2/1951 Mai', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1723, 263, 19710923, 'Die Satzung nennt diesen Termin als Gründungsdatum des Vereins. Ob das Datum auch für die Schachabteilung gilt, ist unklar.', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1724, 263, 19770508, 'Die Schachabteilung von <b>"Berlin Türk Ocagi"</b> (BTO), Türkische Gemeinschaft e.V. Berlin, beantragt die Aufnahme in den Berliner Schachverband, der damals seinen Sitz noch in der Urbanstr.183 hatte. Unterschrieben ist der Antrag vom Abteilungsleiter Oguz Yelkenci und vom Vereinsvorsitzenden Burhan Uysal.

Türk Ocagi hat seinen Sitz in Berlin 44, Pannierstr.40 (Tel. 6234926) und ist unter der Nummer 4356 NZ im Vereinsregister Charlottenburg eingetragen.

Vom BSV erhielt der Verein ein Demonstrationsbrett - ob als Begrüßungsgeschenk ist unbekannt. ', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1725, 263, 19770731, 'BSV-Landesspielleiter Klaus Aepler (1000 Berlin 41, Holsteinische Str.7) teilt dem Verein im Namen des Vorstandes mit, das der Verein mit Entscheidung vom 27.Juli in den Verband aufgenommen wurde. Er bekommt die Vereinsnummer 13.', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1726, 263, 19771106, 'Der Verein teilt dem Verband den Umzug in die Wiener Str.19 in 1000 Berlin 36 mit.', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1727, 263, 19771107, 'Der stellvertretende Vorsitzende des BSV, Alfons Henske, bittet den Verein um die Zahlung der rückständigen Aufnahmegebühr in Höhe von 10 DM. ', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1728, 263, 1980, 'Der Verein nennt sich jetzt <i>Türk Santrac Kulübü</i> - wahrscheinlich ein kleiner Schreibfehler im Dokument des Landessportbundes.', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1729, 263, 19830101, '21 Mitglieder hat der Verein <i>Türk Satranc Kulübü</i> beim BSV gemeldet.', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1730, 263, 19900808, 'Die Senatsverwaltung für Schule, Berufsbildung und Sport streicht den Verein aus der Liste der förderungswürdigen Sportorganisationen. Günter Kroll, wohl damals Verantwortlicher der Schachabteilung oder vielleicht sogar des gesamten Vereins, ist der Empfänger dieses Briefes.', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1731, 264, 19820624, 'Gründungsversammlung des <i>Schachklub Süd-West</i>', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1732, 264, 19820703, 'Der Verein stellt einen Antrag auf Aufnahme in den Berliner Schachverband. Dem Antrag sind die Satzung und eine Mitgliederliste mit 21 Spielern angefügt, u.a. mit Hanna und Horst-Dieter Adam, Bernd Hiller, Helmut Kliem, Anton Manz, Hans-Joachim Sayder, Ludwig Seelentag und Dieter-Bernhard Socher-Löslein.

Vorsitzender ist Anton Manz, seine Stellvertreter "Lu" Seelentag und Hanna Adam. Kassenwart ist Dietrich Bolle.

Ein geeignetes Spiellokal hat der Verein noch nicht gefunden.', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1733, 264, 19820830, 'BSV-Vorsitzender Alfons Henske meldet den Verein dem Landessportbund.', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1734, 264, 19820901, 'Anton Manz bekommt vom Landessportbund einen Brief mit der Aufforderung, einen auf Förderungswürdigkeit beim Senat zu stellen, um Mitglied des Landessportbundes werden zu können.', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1735, 264, 19830101, 'Mit 24 Mitgliedern ist der Verein beim BSV gemeldet. Nicht mehr dabei ist von den oben Genannten Helmut Kliem und Hans-Joachim Sayder. Neu sind u.a. Harry Friedrich, Donald Lemnitz und Andrew Spencer.', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1736, 264, 19920203, 'Sven Schüle informiert den BSV über den Zusammenschluß mit dem [VERWEIS=356]Zehlendorfer Königsjäger e.V.[/VERWEIS] zum [VERWEIS=150]Schachverein Königsjäger Süd-West[/VERWEIS] am 1.Januar 1992. Rein juristisch betrachtet hat sich der <i>SK Süd-West</i> aufgelöst und seine Mitglieder sind dem neuen Verein beigetreten.

Bereits vor der Fusion nahmen die beiden Vereine mit einer Spielgemeinschaft am Spielbetrieb teil.', 'Dokument in BSV-Geschäftsstelle', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1737, 271, 19861128, '<h5>BSG Bako Berlin</h5>
