INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1827, 318, 1954, 'Ein Teil der Schachgruppe zieht nach Berlin-Buch und schließt sich als Schachgruppe der BSG "Medizin Buch" ab 1.10.54 an. Spielort ist das Lokal "Hubertus" am S-Bahnhof Berlin-Buch.', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1828, 319, 1949, 'In der Berliner Jugend-Mannschaftsmeisterschaft siegte in Gruppe I Oberschöneweide, in II Steglitz, in III Hennigsdorf. Beim Endkampf nach dem K.o.-System schaltete Steglitz zwar mit 5:3 Hennigsdorf aus, wurde dann aber selbst von Oberschöneweide mit dem gleichen Ergebnis geschlagen.', 'Deutsche Schachblätter Nr.10 Oktober 1949, S.157/158', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1829, 320, 1950, 'Bei einem Simultanspiel in Berlin-Oberschöneweide traf Teschner auf starke Gegnerschaft, erzielte aber in fast 5 Stunden das gute Ergebnis +26, =3, -2.', 'Deutsche Schachblätter Nr.8 August 1950, S.126', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1830, 320, 1950, 'Vereinsmeister der SG. Union-Oberschöneweide Berlin wurde E.Kaatz 12½ vor Buley 12, Fuchs, Egon Witte 11, Gert Witte 10½. In der 1.Klasse siegte Kilian, in der 2. Marconi.', 'Deutsche Schachblätter Nr.9 September 1950, S.142', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1831, 320, 19500924, 'Die SG Union Oberschöneweide nimmt anläßlich der Handwerksausstellung an einem Vierstädtekampf in Fürstenwalde teil. Am Vormittag siegte Oberschöneweide gegen Frankfurt/Oder mit 18½:1½; am Nachmittag wurde Fürstenwalde (das am Vormittag über Wittenberge erfolgreich war) mit 15½:4½ geschlagen. Jugendmeister Fuchs gewann am Spitzenbrett beide Partien.', 'Deutsche Schachblätter Nr.12 Dezember 1950, S.188', '', 1);
