INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1836, 320, 1952, 'Die Vereinsmeisterschaft 1952 der SG Union Oberschöneweide gewann der Berliner Jugendmeister Wolfram Bialas mit 7 Punkten aus acht Partien. Zweiter wurde Gert Witte mit 5½ Punkten. In der 2.Klasse wurde Erich Höhnow und Wolfgang Krüger mit je 9 Punkten aus zwölf Partien Sieger. In der 3.Klasse belegten Paul Gottmann, Werner Kujawa und Fräulein Falk die ersten drei Plätze. Die Blitzmeisterschaft 1951 gewann ebenfalls Bialas mit 26 Punkten vor G.Wilde (24) und G.Witte (23).', 'SCHACH 9/1952, S.144', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1837, 320, 195204, 'Am Karfreitag trugen Stahl Wilhelmsruh und Union Oberschöneweide einen Freundschaftskampf an 19 Brettern aus. Sieger wurde Union mit 11:8 Punkten.', 'SCHACH 9/1952, S.144', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1838, 320, 1953, 'Zusammenschluß von Motor Oberschöneweide (vormals TRO) und Union Oberschöneweide.', 'Homepage TSG Oberschöneweide', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1839, 321, 1945, 'Frühere Vereinsmitglieder nehmen den Spielbetrieb wieder auf. Es entwickeln sich fast zeitgleich zwei Neuköllner Vereine mit großen Aktivitäten. Der alte Schachklub Neukölln 03 nimmt mit drei spielstarken Mannschaften an den Verbandsmeisterschaften teil.', 'Chronik der Schachfreunde Neukölln', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1840, 321, 1947, 'Die Schachgruppe Neukölln-Nord wird gegründet und wird 1949 zum Verein Neuköllner Schachfreunde. Der damalige Vorsitzende Werner Baranowski hatte den Namen mit Bedacht gewählt: "Freunde, da nicht nur die Pflege des Schachspiels im Vordergrund steht, sondern auch ein geselliger Geist und anständige sportliche Gesinnung." Dieser Verein erreicht ebenfalls ein beachtliches Niveau, auch wenn man es nie ganz schafft, Neukölln 03 einzuholen.', 'Chronik der Schachfreunde Neukölln', '', 1);
