INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1850, 321, 1955, 'Der Schachklub Neukölln 03 erobert den Berliner Mannschaftspokal und spielt während der 50er Jahre regelmäßig in der Berliner Stadtliga.', 'Chronik der Schachfreunde Neukölln', '', 1);
