INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1861, 334, 19520330, 'An diesem Sonntag fand in Görlitz ein Vergleichstreffen zwischen einer Auswahlmannschaft der SV Motor Berlin und dem Gastgeber, der BSG Motor Görlitz, statt. Die Berliner Mannschaft, die sich aus Spielern der BSG Motor Wilhelmsruh und der BSG Motor Treptow zusammensetzte, siegte 10:5. Besonders erfolgreich waren die Jugendlichen aus Wilhelmsruh.', 'SCHACH 8/1952, S.128', '', 1);
